onClick:
{{
  (async () => {
    // Safety checks
    if (!Issues_GetById.data || !Issues_GetById.data.length) {
      showAlert("Load an Issue ID first.", "warning"); return;
    }
    const issue = Issues_GetById.data[0];
    if (String(issue.pic_user_id) !== String(Select_ActingUser.selectedOptionValue)) {
      showAlert("Only the assigned PIC can submit a reply.", "error"); return;
    }
    if (!Input_PICReply.text) {
      showAlert("Reply text is required.", "warning"); return;
    }

    // Insert response, then move status to "Under Review"
    await Responses_Insert.run();
    await Issues_ToUnderReview.run();

    // Refresh the issue row and clear the reply box
    await Issues_GetById.run();
    resetWidget('Input_PICReply', true);

    showAlert("Reply submitted; sent to reviewer.", "success");
  })()
}}
