onClick:
{{
  (async () => {
    // 1) Generate the ID once so we can navigate to it after insert
    const newId = "I" + Date.now();
    await storeValue('NEW_ISSUE_ID', newId);

    // 2) Basic front-end validation (optional but recommended)
    if (!Input_Title.text) { showAlert("Title is required", "warning"); return; }
    if (!AssignedToSelect.selectedOptionValue) { showAlert("PIC is required", "warning"); return; }
    if (!ApprovedBySelect.selectedOptionValue) { showAlert("Reviewer is required", "warning"); return; }
    if (!DepartmentSelect.selectedOptionValue) { showAlert("Department is required", "warning"); return; }

    // 3) Run the insert
    await Issues_Insert.run();

    // 4) Refresh reads (so My Tasks / tables update)
    await Issues_GetAll.run();

    // 5) Notify + navigate to details page of the new issue
    showAlert("Issue created.", "success");
    navigateTo('Issue_Details', { id: newId }, 'SAME_WINDOW');

    // 6) (Optional) clear the form
    resetWidget('Input_Title', true);
    resetWidget('Input_Desc', true);
    resetWidget('Select_PIC', true);
    resetWidget('Select_Reviewer', true);
    resetWidget('Select_Dept', true);

    // 7) Clear temp store
    await storeValue('NEW_ISSUE_ID', undefined);
  })()
}}
